import { useState } from "react";
import FormValidation from "../../utils/FormValidation";

export default function Login({ handleLogin }) {
  const { values, errors, isValid, isInputValid, handleChange } = FormValidation();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // function handleInputEmailChange(e) {
  //   setEmail(e.target.value);
  // }

  // function handleInputPassChange(e) {
  //   setPassword(e.target.value);
  // }

  const handleSubmit = (e) => {
    e.preventDefault();
    handleLogin(values.email, values.password);
  };

  return(
    <div className='auth'>
    <h2 className="auth__title">Вход</h2>
    <form
      noValidate
      onSubmit={handleSubmit}
      className="popup__form popup__form-edit"
      method="POST"
    >
      <label className="popup__input-label">
        <input
          required
          minLength={2}
          maxLength={40}
          className="auth__input"
          id="email"
          name="email"
          placeholder="E-Mail"
          type="text"
          value={values.email}
          onChange={handleChange}
        />
      </label>

      <label className="popup__input-label">
        <input
          required
          minLength={6}
          maxLength={200}
          className="auth__input"
          name="password"
          id="password"
          placeholder="Пароль"
          type="password"
          value={values.password}
          onChange={handleChange}
        />
      </label>

      <button type="submit" className="auth__button" id="login">Авторизоваться</button>
    </form>
    </div>
  )
}
