import { useContext } from "react";
import CurrentUserContext from "../../contexts/CurrentUserContext";

export default function Card({ card, onCardClick, onDeleteCard }) {

  const currentUser = useContext(CurrentUserContext);

  return (
    <> 
      <img className="gallery__photo" src={card.link} alt={card.name} onClick={() => onCardClick(card)} />
      {currentUser._id === card.owner._id && <button className="gallery__button-delete" type="button" title="Удалить!" aria-label="Удалить!" onClick={onDeleteCard} />}

      <div className="gallery__info">
        <h2 className="gallery__title">{card.name}</h2>
        <div className="gallery__like">
          <button
            className="gallery__button-like"
            type="button"
            title="Лайк!"
            aria-label="Лайк!"
          />
          <span className="gallery__button-like-counter" />
        </div>
      </div>
    </>
  )
}
