export default function Login() {
  return(
    <>
    <h2 className="popup__title">{title}</h2>
    <form
      noValidate
      className="popup__form popup__form-edit"
      id={`${name}-form`}
      name={`${name}-form`}
      method="POST"
      onSubmit={onSubmit}
    >
      {children}
      <button type="submit" className={`popup__button ${isSend ? 'popup__button_loading' : ''} ${isValid ? '' : 'popup__button_disabled'}`} id="save-profile" disabled={isSend}>
        {isSend ? '' : buttonTitle||'Сохранить'}
      </button>
    </form>
    </>
  )
}
