export default function InfoToolTip({ isOpen, onClose, isSuccess }) {
  return(
    <div className={`popup  ${isOpen && 'popup_opened'}`} onClick={onClose}>
    <div className="popup__edit-form" onClick={(evt => evt.stopPropagation())}>
    <button
        type="button"
        className="popup__button-close popup__button-close_photo"
        onClick={onClose}
      />

      <span className="popup__info-error">{isSuccess ? 'Вы успешно зарегистрировались!' : 'Лох, пробуй снова'}</span>


    </div>
  </div>
    )
}
