import { useContext, useEffect, useState } from "react"
import api from "../../utils/api";
import Card from "../Card/Card";
import EditAvatar from "../../images/ava-edit.png"
import CurrentUserContext from "../../contexts/CurrentUserContext";

export default function Main({ onEditProfile, onAddPlace, onEditAvatar, onCardClick, onDeleteCard }) {

  const currentUser  = useContext(CurrentUserContext);

  // const [userName, setUserName] = useState('')
  // const [userDescription, setUserDescription] = useState('');
  // const [userAvatar, setUserAvatar] = useState('');
  const [cards, setCards] = useState([])

  useEffect(() => {
    Promise.all([api.getInfo(), api.getInitialCards()])
      .then(([userData, cardData]) => {
        setUserName(userData.name)
        setUserDescription(userData.about)
        setUserAvatar(userData.avatar)
        cardData.forEach(el => el.myId = userData._id)
        setCards(cardData)
      })
      .catch((err) => {
        console.error(`Не удалось загрузить данные: ${err}`)
      })
  }, [])

  return(
    <main className="content">
      <section className="profile">
        <div className="profile__avatar">
          <img
            src={currentUser.avatar}
            alt="Аватар пользователя"
            className="profile__avatar-img"
          />
          <div className="profile__avatar-overlay">
            <img
              className="profile__avatar-edit"
              src={EditAvatar}
              alt="Редактировать аватар"
              onClick={onEditAvatar}
            />
          </div>
        </div>
        <div className="profile__info">
          <div className="profile__user">
            <h1 className="profile__name">{userName}</h1>
            <button
              className="profile__button-edit"
              type="button"
              title="Редактировать профиль"
              aria-label="Редактировать профиль"
              onClick={onEditProfile}
            />
          </div>
          <p className="profile__description">{userDescription}</p>
        </div>
        <button
          className="profile__button-add"
          type="button"
          title="Добавить"
          aria-label="Добавить"
          onClick={onAddPlace}
        />
      </section>
      <section className="gallery">
        <ul className="gallery__list">
          {cards.map(item => {
            return (
              <li className="gallery__item" key={item._id}>
                <Card card={item} onCardClick={onCardClick} onDeleteCard={onDeleteCard} />
              </li>
            )
          })}
        </ul>
      </section>
    </main>
    )
}
