import { useEffect, useState } from "react"
import api from "../../utils/api";

export default function ButtonLike({ likes, myId, cardId }) {

  const [isLiked, setIsLiked] = useState(false);
  const [count, setCount] = useState(likes.length);

  useEffect(() => {
    setIsLiked(likes.some(el => myId === el._id))
  }, [likes, myId])

  function handleLike() {
    if (isLiked) {
      api.removeLike(cardId)
        .then(res => {
          setIsLiked(false)
          setCount(res.likes.length)
        })
        .catch((err) => console.error(`Не удалось снять лайк: ${err}`))
    } else {
      api.addLike(cardId)
        .then(res => {
          setIsLiked(true)
          setCount(res.likes.length)
        })
        .catch((err) => console.error(`Не удалось поставить лайк: ${err}`))
    }
  }

  return (
    <>
      <button
        className={`gallery__button-like ${isLiked ? 'gallery__button-like_active' : ''}`}
        type="button"
        title="Лайк!"
        aria-label="Лайк!"
        onClick={handleLike}
      />
      <span className="gallery__button-like-counter">{count}</span>
    </>
  )
}
