import { useEffect, useState } from "react"
import api from "../../utils/api";

export default function ButtonLike({ likes, myId, cardId }) {

  const[isLike, setIsLike] = useState(false);
  const[count, setCount] = useState(likes.length);

  useEffect(() => {
    setIsLike(likes.some(el => myId === el._id))
  },[likes, myId])

  function handleLike() {
    if (isLike) {
      api.removeLike(cardId)
        .then(res => {
          setIsLike(false)
          setCount(res.likes.length)
        })
        .catch((err) => console.error(`Не удалось загрузить данные: ${err}`))
    } else {
      api.addLike(cardId)
    }
  }

  return (
    <>
     <button
            className="gallery__button-like"
            type="button"
            title="Лайк!"
            aria-label="Лайк!" 
            onClick={handleLike}
          />
          <span className="gallery__button-like-counter">{count}</span>
    </>
  )
}
