import PopupWithForm from "../PopupWithForm/PopupWithForm"
import useFormValidation from "../../utils/useFormValidation"

export default function EditProfilePopup({ isOpen, onClose }) {
  const { values, errors, isValid, isInputValidhandleChange } = useFormValidation();

  return(
    <PopupWithForm 
    name='profile'
    title='Редактировать профиль'
    isOpen={isOpen}
    onClose={onClose}
    >
      <label className="popup__input-label">
        <input
          required=""
          minLength={2}
          maxLength={40}
          className="popup__input popup__input_name"
          id="username"
          name="username"
          placeholder="Имя"
          type="text"
          onChange={handleChange}
        />
        <span id="username-error" className="popup__error" />
      </label>
      <label className="popup__input-label">
        <input
          required=""
          minLength={2}
          maxLength={200}
          className="popup__input popup__input_description"
          name="description"
          id="description"
          placeholder="О себе"
          type="text"
          onChange={handleChange}
        />
        <span id="description-error" className="popup__error" />
      </label>
    </PopupWithForm>
    )
}
