import logo from '../../images/logo.svg'
import { Route, Link, Routes } from "react-router-dom";



export default function Header({ email }) {

  function handleSignOut() {
    localStorage.removeItem('jwt')
  }

  return(
    <header className="header">
      <a href="/"><img
        src={logo}
        className="header__logo"
        alt="Логотип проекта"
      /></a>
      <div></div>
      <span className="header__mail">{email}</span>
      <Routes>
      <Route path="/sign-in" element={<a href="/sign-up" className='header__mail'>Регистрация</a>}>

        </Route>
        <Route path="/sign-up" element={<a href="/sign-in" className='header__mail'>Войти</a>}>

        </Route>
        <Route path="/" element={<a onClick={handleSignOut} href="/sign-in" className='header__auth-button'>Выйти</a>}>
        </Route>

      </Routes>


    </header>
  )
}
