import { useCallback, useEffect, useState } from 'react';
import CurrentUserContext from '../../contexts/CurrentUserContext';
import api from '../../utils/api';

import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import Main from '../Main/Main';

import PopupWithForm from '../PopupWithForm/PopupWithForm';
import ImagePopup from '../ImagePopup/ImagePopup';
import EditProfilePopup from '../EditProfilePopup/EditProfilePopup';
import EditAvatarPopup from '../EditAvatarPopup/EditAvatarPopup';
import AddPlacePopup from '../AddPlacePopup/AddPlacePopup';


function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isDeletePopupOpen, setDeletePopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);

  const [isSend, setIsSend] = useState(false);
  const [currentUser, setCurrentUser] = useState({})

  const [cards, setCards] = useState([]);
  const [isLoadingCards, setIsLoadingCards] = useState(true);
  const [deleteCardId, setDeleteCardId] = useState('');


  const setStatesForClosePopups = useCallback(() => {
    setIsEditProfilePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setDeletePopupOpen(false);
    setSelectedCard(null);
  }, [])

  const closePopupByEsc = useCallback((evt) => {
    if (evt.key === 'Escape') {
      setStatesForClosePopups()
      document.removeEventListener('keydown', closePopupByEsc)
    }
  }, [setStatesForClosePopups])

  const closeAllPopups = useCallback(() => {
    setStatesForClosePopups()
    document.removeEventListener('keydown', closePopupByEsc)
  }, [setStatesForClosePopups, closePopupByEsc])

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
    setEventListenerEsc()
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
    setEventListenerEsc()
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
    setEventListenerEsc()
  }

  function handleCardClick(card) {
    setSelectedCard(card)
    setEventListenerEsc()
  }

  function handleCardDelete(cardId) {
    setDeleteCardId(cardId);
    setDeletePopupOpen(true);
    setEventListenerEsc();
  }

  function setEventListenerEsc() {
    document.addEventListener('keydown', closePopupByEsc)
  }

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
      })
      .catch((err) => console.log(err));
  }

  function handleDeleteCard(evt) {
    evt.preventDefault()
    setIsSend(true)
    api.deleteCard(deleteCardId)
      .then(() => {
        setCards(cards.filter(item => {
          return item._id !== deleteCardId
        }))
        closeAllPopups();
        setIsSend(false)
      })
      .catch((err) => console.error(`Не удалось удалить карточку: ${err}`))
      .finally(() => setIsSend(false))
  }

  useEffect(() => {
    setIsLoadingCards(true);
    Promise.all([api.getInfo(), api.getInitialCards()])
      .then(([userData, cardData]) => {
        setCurrentUser(userData)
        setCards(cardData)
        setIsLoadingCards(false)
      })
      .catch((err) => {
        console.error(`Не удалось загрузить данные: ${err}`)
      })
  }, [])

  function handleUpdateUser(dataUser, reset) {
    setIsSend(true)
    api.setUserInfo(dataUser)
      .then(res => {
        setCurrentUser(res)
        closeAllPopups()
        reset()
        setIsSend(false)
      })
      .catch((err) => console.error(`Не удалось обновить данные профиля: ${err}`))
      .finally(() => setIsSend(false))
  }

  function handleUpdateAvatar(dataUser, reset) {
    setIsSend(true)
    api.setAvatar(dataUser)
      .then(res => {
        setCurrentUser(res)
        closeAllPopups()
        reset()
        setIsSend(false)
      })
      .catch((err) => console.error(`Не удалось обновить аватар: ${err}`))
      .finally(() => setIsSend(false))
  }

  function handleAddPlaceSubmit(dataCard, reset) {
    setIsSend(true)
    api.addCard(dataCard)
      .then(newCard => {
        setCards([newCard, ...cards])
        closeAllPopups()
        reset()
        setIsSend(false)
      })
      .catch((err) => console.error(`Не удалось добавить карточку: ${err}`))
      .finally(() => setIsSend(false))
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page__content">
        <Header />
        <Main

          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClick={handleCardClick}
          onDeleteClick={handleCardDelete}
          cards={cards}
          isLoadingCards={isLoadingCards}
        />
        <Footer />
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
          isSend={isSend}
          buttonTitle='Сохранить'
        />

        <AddPlacePopup
          onAddPlace={handleAddPlaceSubmit}
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          isSend={isSend}
        />

        <EditAvatarPopup
          onUpdateAvatar={handleUpdateAvatar}
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          isSend={isSend}
        />
        <PopupWithForm
          name='delete_card'
          title='Вы уверены?'
          buttonTitle='Да'
          isOpen={isDeletePopupOpen}
          onClose={closeAllPopups}
          onSubmit={handleDeleteCard}
          isSend={isSend}
        />

        <ImagePopup
          card={selectedCard}
          onClose={closeAllPopups}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
