export default function Login() {

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function handleInputEmailChange(e) {
    setEmail(e.target.value);
  }

  function handleInputPassChange(e) {
    setPassword(e.target.value);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!email || !password) {
      return;
    }
    props.handleLogin(email, password).catch(console.log);
  };

  return(
    <div className='auth'>
    <h2 className="auth__title">Вход</h2>
    <form
      noValidate
      onSumbit={handleSubmit}
      className="popup__form popup__form-edit"
      method="POST"
    >
      <label className="popup__input-label">
        <input
          required
          minLength={2}
          maxLength={40}
          className="auth__input"
          id="email"
          name="email"
          placeholder="Имя"
          type="text"
          value={email}
        />
      </label>
      <label className="popup__input-label">
        <input
          required
          minLength={2}
          maxLength={200}
          className="auth__input"
          name="description"
          id="description"
          placeholder="О себе"
          type="text"
        />
      </label>
      <button type="submit" className="auth__button" id="save-profile">
        {'Сохранить'}
      </button>
    </form>
    </div>
  )
}
