export default function PopupImage({ card, isOpen, onClose }) {
  return(
    <div className={`popup popup_photo ${card && 'popup_opened'}`} style={{ display: `${card ? 'none' : 'block'}` }} >
    <div className="popup__photo">
      <img className="popup__photo-source" alt={card?.name} src={card?.link} />
      <h3 className="popup__photo-title">{card?.name}</h3>
      <button
        type="button"
        className="popup__button-close popup__button-close_photo"
        onClick={onClose}
      />
    </div>
  </div>
    )
}
