import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import PopupWithForm from '../PopupWithForm/PopupWithForm';
import Main from '../Main/Main';
import ImagePopup from '../ImagePopup/ImagePopup';
import { useEffect, useState } from 'react';
import CurrentUserContext from '../../contexts/CurrentUserContext';
import api from '../../utils/api';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isDeletePopupOpen, setDeletePopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);

  const [currentUser, setCurrentUser] = useState({})

  const [cards, setCards] = useState([]);
  const [isLoadingCards, setIsLoadingCards] = useState(true);


  function closePopupByEsc(evt) {
    if (evt.key === 'Escape') {
      closeAllPopups()
      document.removeEventListener('keydown', closePopupByEsc)
    }
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setDeletePopupOpen(false);
    setSelectedCard(null);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
    setEventListenerEsc()
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
    setEventListenerEsc()
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
    setEventListenerEsc()
  }

  function handleCardClick(card) {
    setSelectedCard(card)
    setEventListenerEsc()
  }

  function handleDeleteCardClick() {
    setDeletePopupOpen(true);
    setEventListenerEsc();
  }

  function overlayClosePopup(evt) {
    if (evt.target === evt.currentTarget) {
      closeAllPopups();
    }
  }

  function setEventListenerEsc() {
    document.addEventListener('keydown', closePopupByEsc )
  }



  useEffect(() => {
    setIsLoadingCards(true);
    Promise.all([api.getInfo(), api.getInitialCards()])
      .then(([userData, cardData]) => {
        setCurrentUser(userData)
        setCards(cardData)
        setIsLoadingCards(false)
      })
      .catch((err) => {
        console.error(`Не удалось загрузить данные: ${err}`)
      })
  }, [])

  return (
    <CurrentUserContext.Provider value={currentUser}>

    <div className="page__content">
      <Header />
      <Main 
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onEditAvatar={handleEditAvatarClick}
        onCardClick={handleCardClick}
        onDeleteClick={handleDeleteCardClick}
        cards={cards}
      />
      <Footer />
      <PopupWithForm 
        name='profile'
        title='Редактировать профиль'
        isOpen={isEditProfilePopupOpen}
        onClose={overlayClosePopup}
      >
        <label className="popup__input-label">
          <input
            required=""
            minLength={2}
            maxLength={40}
            className="popup__input popup__input_name"
            id="username"
            name="username"
            placeholder="Имя"
            type="text"
          />
          <span id="username-error" className="popup__error" />
        </label>
        <label className="popup__input-label">
          <input
            required=""
            minLength={2}
            maxLength={200}
            className="popup__input popup__input_description"
            name="description"
            id="description"
            placeholder="О себе"
            type="text"
          />
          <span id="description-error" className="popup__error" />
        </label>
      </PopupWithForm>
      <PopupWithForm
        name='add_card'
        title='Новое место'
        buttonTitle='Создать'
        isOpen={isAddPlacePopupOpen}
        onClose={overlayClosePopup}
      >
        <label className="popup__input-label">
          <input
            required=""
            minLength={2}
            maxLength={30}
            className="popup__input popup__input_card-name"
            id="card-name"
            name="name"
            placeholder="Название"
            type="text"
          />
          <span id="card-name-error" className="popup__error" />
        </label>
        <label className="popup__input-label">
          <input
            required=""
            type="url"
            className="popup__input popup__input_card-link"
            id="card-link"
            name="link"
            placeholder="Ссылка на картинку"
          />
          <span id="card-link-error" className="popup__error" />
        </label>
      </PopupWithForm>

      <PopupWithForm
        name='edit_avatar'
        title='Обновить аватар'
        isOpen={isEditAvatarPopupOpen}
        onClose={overlayClosePopup}
      >
        <label className="popup__input-label">
          <input
            required=""
            type="url"
            className="popup__input popup__input_avatar-link"
            id="avatar"
            name="avatar"
            placeholder="Ссылка на аватар"
          />
          <span id="avatar-error" className="popup__error" />
        </label>
      </PopupWithForm>

      <PopupWithForm 
        name='delete_card'
        title='Вы уверены?' 
        buttonTitle='Да' 
      />

      <ImagePopup 
        card={selectedCard}
        onClose={overlayClosePopup}
      />
    </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
