export default function PopupWithForm({ name, title, buttonTitle, children, isOpen, onClose }) {
  return (
    <div className={`popup popup__${name} ${isOpen && 'popup_opened'}`}>
    <div className="popup__edit-form">
      <button
        className="popup__button-close popup__button-close-profile"
        title="Закрыть окно"
        type="button"
        aria-label="Закрыть окно"
        onClick={onClose}
      />
      <h2 className="popup__title">{title}</h2>
      <form
        noValidate=""
        className="popup__form popup__form-edit"
        id={`${name}-form`}
        name={`${name}-form`}
        method="POST"
      >
        {children}
        <button type="submit" className="popup__button" id="save-profile">
          {buttonTitle||'Сохранить'}
        </button>
      </form>
    </div>
  </div>
    )
}
