import { useState } from "react";

export default function Register({ handleRegister }) {

  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("123");

  function handleInputEmailChange(e) {
    setEmail(e.target.value);
  }

  function handleInputPassChange(e) {
    setPassword(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    handleRegister(password, email).catch(setMessage);
  }

  return(
    <div className='auth'>
    <h2 className="auth__title">Регистрация</h2>
    <form
      noValidate
      className="popup__form popup__form-edit"
      method="POST"
      onSubmit={handleSubmit}
    >
      <label className="popup__input-label">
        <input
          required
          minLength={2}
          maxLength={40}
          className="auth__input"
          id="email"
          name="email"
          placeholder="email"
          type="email"
          value={email}
          onChange={handleInputEmailChange}
        />
      </label>
      <label className="popup__input-label">
        <input
          required
          minLength={2}
          maxLength={200}
          className="auth__input"
          name="password"
          id="password"
          placeholder="password"
          type="password"
          value={password}
          onChange={handleInputPassChange}
        />
      </label>
      <button type="submit" className="auth__button">Регистрация</button>
    </form>
    </div>
  )
}
