import { useContext } from "react";
import CurrentUserContext from "../../contexts/CurrentUserContext";
import ButtonLike from "../ButtonLike/ButtonLike";

export default function Card({ card, onCardClick, onDeleteCardClick }) {

  const currentUser = useContext(CurrentUserContext);


  return (
    <>
      <img className="gallery__photo" src={card.link} alt={card.name} onClick={() => onCardClick(card)} />
      {currentUser._id === card.owner._id && <button className="gallery__button-delete" type="button" title="Удалить!" aria-label="Удалить!" onClick={() => onDeleteCardClick(card._id)} />}

      <div className="gallery__info">
        <h2 className="gallery__title">{card.name}</h2>
        <div className="gallery__like">
          <ButtonLike likes={card.likes} myId={currentUser._id} cardId={card._id} />
        </div>
      </div>
    </>
  )
}
